<?php     // Get lat and long by address                 $address = $_GET['keyword']; // Google HQ        $prepAddr = str_replace(' ','+',$address);        $geocode=file_get_contents('https://maps.google.com/maps/api/geocode/json?address='.$prepAddr.'&sensor=false');        $output= json_decode($geocode);        $latitude = $output->results[0]->geometry->location->lat;        $longitude = $output->results[0]->geometry->location->lng;?><div id="lo">						<label >Latitude</label>						<input type="text" id="latitude" name="latitude" value="<?php echo $latitude?>" />						<label >Longitude</label>						<input type="text" id="longitude" name="longitude" value="<?php echo ("ok")?>" />	</div>